    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        media = ((Double.parseDouble(txN1.getText())+ Double.parseDouble(txN2.getText())+ Double.parseDouble(txN3.getText())) /3);
        if (media >= 7)JOptionPane.showMessageDialog(null,"Nome: " + txNome.getText() + "\nMédia: " + media + "\nSituação: Aprovado!" );
        else if (media > 5) JOptionPane.showMessageDialog(null,"Nome: " + txNome.getText() + "\nMédia: " + media + "\nSituação: Recuperação!" );
        else JOptionPane.showMessageDialog(null,"Nome: " + txNome.getText() + "\nMédia: " + media + "\nSituação: Reprovado!" );
}//GEN-LAST:event_jButton1ActionPerformed

